  export let accent: string = '#565656';

  let rootEl: HTMLElement | null = null;
  let visible = false;

  onMount(() => {
    if (!rootEl) return;
    const obs = new IntersectionObserver((entries) => {
      for (const e of entries) {
        if (e.isIntersecting) {
          visible = true;
          obs.disconnect();
        }
      }
    }, { threshold: 0.12 });
    obs.observe(rootEl);
  });
</script>

